        public string? BackHandOpen { get; set; } = null;

        public string? Latex { get; set; } = null;

        public string? Cotton { get; set; } = null;

        public string? FingersOpen { get; set; } = null;

        public string? Cleaning { get; set; } = null;

        public string? Sport { get; set; } = null;

        public string? OP { get; set; } = null;

        public string? Green { get; set; } = null;

        public string? BluePatternDark { get; set; } = null;

        public string? BluePatternLight { get; set; } = null;

        public string? Motorcycle { get; set; } = null;

        public string? Special { get; set; } = null;

        public bool HasTorso(string torsoHash) => None == torsoHash || BackHandOpen == torsoHash || Latex == torsoHash || Cotton == torsoHash ||
            FingersOpen == torsoHash || Cleaning == torsoHash || Sport == torsoHash || OP == torsoHash || Green == torsoHash ||
            BluePatternDark == torsoHash || BluePatternLight == torsoHash || Motorcycle == torsoHash || Special == torsoHash;

        /// <summary>
        /// Hole Torso anhand Type
        /// </summary>
        /// <param name="glove"></param>
        /// <returns></returns>
        public string? GetGloveByType(Glove glove) => glove switch
        {
            Glove.BackHandOpen => BackHandOpen,
            Glove.Latex => Latex,
            Glove.Cotton => Cotton,
            Glove.FingersOpen => FingersOpen,
            Glove.Cleaning => Cleaning,
            Glove.Sport => Sport,
            Glove.OP => OP,
            Glove.Green => Green,
            Glove.BluePatternDark => BluePatternDark,
            Glove.BluePatternLight => BluePatternLight,
            Glove.Motorcycle => Motorcycle,
            _ => None,
        };

        /// <summary>
        /// Lade Type anhand Torso
        /// </summary>
        /// <param name="torsoHash"></param>
        /// <returns></returns>
        public Glove GetGloveByTorso(string torsoHash)
        {
            if (BackHandOpen == torsoHash) return Glove.BackHandOpen;
            else if (Latex == torsoHash) return Glove.Latex;
            else if (Cotton == torsoHash) return Glove.Cotton;
            else if (FingersOpen == torsoHash) return Glove.FingersOpen;
            else if (Cleaning == torsoHash) return Glove.Cleaning;
            else if (Sport == torsoHash) return Glove.Sport;
            else if (OP == torsoHash) return Glove.OP;
            else if (Green == torsoHash) return Glove.Green;
            else if (BluePatternDark == torsoHash) return Glove.BluePatternDark;
            else if (BluePatternLight == torsoHash) return Glove.BluePatternLight;
            else if (Motorcycle == torsoHash) return Glove.Motorcycle;
            return Glove.None;
        }
